tool.command:
#if str($cond_download.select_download) == "yes": wget --no-verbose 'https://data.broadinstitute.org/Trinity/Trinotate_v3_RESOURCES/Trinotate_v3.sqlite.gz' -O Trinotate.sqlite.gz && gunzip Trinotate.sqlite.gz && #else: ln -s '$cond_download.trinotate_sqlite_in' Trinotate.sqlite #end if
annotation2g--gene_trans_map '$gene_trans_map' --transcript_fasta $trinity --transdecoder_pep '$transdecoder' && #if $blastp: Trinotate Trinotate.sqlite LOAD_swissprot_blastp '$blastp' && #end if #if $blastx: Trinotate Trinotate.sqlite LOAD_swissprot_blastx '$blastx' && #end if #if $hmmscan: Trinotate Trinotate.sqlite LOAD_pfam '$hmmscan' && #end if #if $tmhmm: Trinotate Trinotate.sqlite LOAD_tmhmm '$tmhmm' && #end if #if $signalp: Trinotate Trinotate.sqlite LOAD_signalp '$signalp' && #end if
Trinotate Trinotate.sqlite report -E $report_option.E --pfam_cutoff $report_option.pfam_cutoff $report_option.incl_pep $report_option.incl_trans > '$trinotate_out';
